import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Box, Drawer, List, IconButton, Tooltip, Typography } from '@mui/material';
import { styled } from '@mui/material/styles';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import { useNavigationStore } from '@/store/navigationStore';
import { useAuthStore } from '@/store/authStore';
import { NavigationItem } from './NavigationItem';
import { navigationConfig } from '../../config/navigation.config';
import { useNavigationShortcuts } from '@/hooks/useNavigationShortcuts';

const DRAWER_WIDTH = 320; // Erh√∂ht von 280px f√ºr bessere Textdarstellung
const DRAWER_WIDTH_COLLAPSED = 64;

const StyledDrawer = styled(Drawer, {
  shouldForwardProp: (prop) => prop !== 'collapsed',
})<{ collapsed: boolean }>(({ theme, collapsed }) => ({
  width: collapsed ? DRAWER_WIDTH_COLLAPSED : DRAWER_WIDTH,
  flexShrink: 0,
  whiteSpace: 'nowrap',
  boxSizing: 'border-box',
  '& .MuiDrawer-paper': {
    width: collapsed ? DRAWER_WIDTH_COLLAPSED : DRAWER_WIDTH,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
    overflowX: 'hidden',
    backgroundColor: '#FAFAFA', // Leicht grauer Hintergrund f√ºr besseren Kontrast
    borderRight: '2px solid #94C456', // Freshfoodz Gr√ºn als Akzent
    boxShadow: '2px 0 8px rgba(0, 0, 0, 0.05)',
  },
}));

export const SidebarNavigation: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { userPermissions } = useAuthStore();
  const {
    activeMenuId,
    expandedMenuId,
    isCollapsed,
    setActiveMenu,
    toggleSubmenu,
    closeAllSubmenus,
    toggleSidebar,
    addToRecentlyVisited
  } = useNavigationStore();

  // Keyboard shortcuts
  useNavigationShortcuts();

  // Track visited pages
  useEffect(() => {
    addToRecentlyVisited(location.pathname);
  }, [location.pathname, addToRecentlyVisited]);

  // Auto-set active menu based on current URL
  useEffect(() => {
    const currentPath = location.pathname;

    // Find matching navigation item (including sub-items)
    const matchingItem = navigationConfig.find(item => {
      // Direct path match
      if (currentPath.startsWith(item.path)) {
        return true;
      }

      // Sub-item path match
      if (item.subItems) {
        return item.subItems.some(subItem => currentPath.startsWith(subItem.path));
      }

      return false;
    });

    if (matchingItem) {
      // Always set the active menu to ensure correct highlighting
      setActiveMenu(matchingItem.id);

      // Check if we're on a sub-page
      const isOnSubPage = matchingItem.subItems?.some(sub => currentPath.startsWith(sub.path));

      // Auto-expand submenu if on a sub-page and not already expanded
      if (isOnSubPage && expandedMenuId !== matchingItem.id) {
        toggleSubmenu(matchingItem.id);
      }
    }
  }, [location.pathname]); // Remove dependencies to always update on path change

  // Filter navigation items based on permissions
  const visibleItems = navigationConfig.filter(item => 
    !item.permissions || item.permissions.some(p => userPermissions.includes(p))
  );

  return (
    <StyledDrawer
      variant="permanent"
      collapsed={isCollapsed}
    >
      <Box sx={{ 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'space-between',
        p: 2,
        minHeight: 64,
        borderBottom: '1px solid rgba(148, 196, 86, 0.2)',
      }}>
        {!isCollapsed && (
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Box
              component="img"
              src="/freshfoodz-logo.svg"
              alt="FreshPlan"
              sx={{ height: 32 }}
              onError={(e) => {
                const target = e.target as HTMLImageElement;
                target.style.display = 'none';
              }}
            />
            <Typography
              variant="h6"
              sx={{
                color: '#004F7B',
                fontFamily: 'Antonio, sans-serif',
                fontWeight: 700,
              }}
            >
              FreshPlan
            </Typography>
          </Box>
        )}
        <Tooltip title={isCollapsed ? "Navigation erweitern" : "Navigation einklappen"}>
          <IconButton 
            onClick={toggleSidebar} 
            size="small"
            sx={{
              color: '#94C456',
              '&:hover': {
                backgroundColor: 'rgba(148, 196, 86, 0.1)',
              },
            }}
          >
            {isCollapsed ? <ChevronRightIcon /> : <ChevronLeftIcon />}
          </IconButton>
        </Tooltip>
      </Box>

      <List component="nav" sx={{ px: 1 }}>
        {visibleItems.map((item) => (
          <NavigationItem
            key={item.id}
            item={item}
            isActive={activeMenuId === item.id}
            isExpanded={expandedMenuId === item.id}
            isCollapsed={isCollapsed}
            onItemClick={() => {
              setActiveMenu(item.id);
              if (!item.subItems) {
                // Navigate to the page and close any open submenus
                navigate(item.path);
                closeAllSubmenus();
              } else {
                toggleSubmenu(item.id);
              }
            }}
            onSubItemClick={(subPath) => {
              navigate(subPath);
              setActiveMenu(item.id);
            }}
          />
        ))}
      </List>
    </StyledDrawer>
  );
};

/**
 * Enterprise Toast Notification System
 * 
 * @module ToastProvider
 * @description Zentrales Toast-System f√ºr alle Benachrichtigungen im CRM.
 *              Unterst√ºtzt verschiedene Severity-Level, Auto-Dismiss und Actions.
 * @since 2.0.0
 * @author FreshPlan Team
 */

import React, { createContext, useContext, useState, useCallback } from 'react';
import {
  Alert,
  AlertColor,
  IconButton,
  Box,
  Typography,
  Button
} from '@mui/material';
import { Close as CloseIcon } from '@mui/icons-material';

/**
 * Toast notification type
 */
export interface Toast {
  id: string;
  message: string;
  title?: string;
  severity: AlertColor;
  duration?: number;
  action?: {
    label: string;
    onClick: () => void;
  };
  persistent?: boolean;
}

/**
 * Toast context type
 */
interface ToastContextType {
  addToast: (toast: Omit<Toast, 'id'>) => void;
  removeToast: (id: string) => void;
  clearAll: () => void;
}

/**
 * Toast context
 */
const ToastContext = createContext<ToastContextType | undefined>(undefined);

/**
 * Hook to use toast notifications
 */
export const useToast = () => {
  const context = useContext(ToastContext);
  if (!context) {
    throw new Error('useToast must be used within a ToastProvider');
  }
  return context;
};

/**
 * Enhanced toast hook with predefined methods
 */
export const useEnhancedToast = () => {
  const { addToast, removeToast, clearAll } = useToast();

  const showSuccess = useCallback((message: string, title?: string, action?: Toast['action']) => {
    addToast({
      message,
      title,
      severity: 'success',
      duration: 4000,
      action
    });
  }, [addToast]);

  const showError = useCallback((message: string, title?: string, persistent = false) => {
    addToast({
      message,
      title,
      severity: 'error',
      duration: persistent ? undefined : 6000,
      persistent
    });
  }, [addToast]);

  const showWarning = useCallback((message: string, title?: string, action?: Toast['action']) => {
    addToast({
      message,
      title,
      severity: 'warning',
      duration: 5000,
      action
    });
  }, [addToast]);

  const showInfo = useCallback((message: string, title?: string, action?: Toast['action']) => {
    addToast({
      message,
      title,
      severity: 'info',
      duration: 4000,
      action
    });
  }, [addToast]);

  return {
    showSuccess,
    showError,
    showWarning,
    showInfo,
    removeToast,
    clearAll
  };
};

/**
 * Toast provider component
 */
export const ToastProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = useCallback((toastData: Omit<Toast, 'id'>) => {
    const id = Math.random().toString(36).substr(2, 9);
    const toast: Toast = {
      ...toastData,
      id
    };

    setToasts(prev => [...prev, toast]);

    // Auto-dismiss nach duration (falls nicht persistent)
    if (!toast.persistent && toast.duration !== undefined) {
      setTimeout(() => {
        removeToast(id);
      }, toast.duration);
    }
  }, []);

  const removeToast = useCallback((id: string) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  }, []);

  const clearAll = useCallback(() => {
    setToasts([]);
  }, []);

  return (
    <ToastContext.Provider value={{ addToast, removeToast, clearAll }}>
      {children}

      {/* Render all toasts in a container */}
      <Box
        sx={{
          position: 'fixed',
          top: 80,
          right: 24,
          zIndex: (theme) => theme.zIndex.snackbar,
          display: 'flex',
          flexDirection: 'column',
          gap: 1
        }}
      >
        {toasts.map((toast) => (
          <Alert
            key={toast.id}
            severity={toast.severity}
            variant="filled"
            onClose={() => removeToast(toast.id)}
            action={
              <Box display="flex" alignItems="center" gap={1}>
                {toast.action && (
                  <Button
                    color="inherit"
                    size="small"
                    onClick={toast.action.onClick}
                    sx={{ mr: 1 }}
                  >
                    {toast.action.label}
                  </Button>
                )}
                <IconButton
                  size="small"
                  aria-label="close"
                  color="inherit"
                  onClick={() => removeToast(toast.id)}
                >
                  <CloseIcon fontSize="small" />
                </IconButton>
              </Box>
            }
            sx={{
              minWidth: 320,
              maxWidth: 500,
              boxShadow: 3,
              '& .MuiAlert-message': {
                width: '100%'
              }
            }}
          >
            {toast.title && (
              <Typography variant="subtitle2" sx={{ fontWeight: 'bold', mb: 0.5 }}>
                {toast.title}
              </Typography>
            )}
            <Typography variant="body2">
              {toast.message}
            </Typography>
          </Alert>
        ))}
      </Box>
    </ToastContext.Provider>
  );
};

/**
 * Predefined toast notifications for common CRM actions
 */
export const CrmToasts = {
  /**
   * Stage change notifications
   */
  stageChanged: (stageName: string, opportunityName: string) => ({
    title: 'Stage ge√§ndert',
    message: `"${opportunityName}" wurde zu "${stageName}" verschoben.`,
    severity: 'success' as AlertColor,
    duration: 3000
  }),

  stageChangeFailed: (error: string) => ({
    title: 'Stage-Wechsel fehlgeschlagen',
    message: `Fehler beim Verschieben: ${error}`,
    severity: 'error' as AlertColor,
    duration: 5000
  }),

  /**
   * Renewal notifications
   */
  renewalStarted: (customerName: string) => ({
    title: 'Renewal-Prozess gestartet',
    message: `Vertragsverl√§ngerung f√ºr "${customerName}" wurde eingeleitet.`,
    severity: 'info' as AlertColor,
    duration: 4000,
    action: {
      label: 'Details',
      onClick: () => {
        // Navigate to renewal details
        console.log('Navigate to renewal details');
      }
    }
  }),

  renewalCompleted: (customerName: string, isSuccessful: boolean) => ({
    title: isSuccessful ? 'Renewal erfolgreich' : 'Renewal nicht erfolgreich',
    message: isSuccessful 
      ? `Vertrag mit "${customerName}" wurde erfolgreich verl√§ngert.`
      : `Renewal mit "${customerName}" konnte nicht abgeschlossen werden.`,
    severity: (isSuccessful ? 'success' : 'warning') as AlertColor,
    duration: 5000
  }),

  /**
   * Deal close notifications
   */
  dealWon: (opportunityName: string, value?: number) => ({
    title: 'üéâ Deal gewonnen!',
    message: `"${opportunityName}" wurde erfolgreich abgeschlossen${value ? ` (‚Ç¨${value.toLocaleString()})` : ''}.`,
    severity: 'success' as AlertColor,
    duration: 6000,
    action: {
      label: 'Onboarding starten',
      onClick: () => {
        // Navigate to onboarding
        console.log('Start onboarding process');
      }
    }
  }),

  dealLost: (opportunityName: string, reason?: string) => ({
    title: 'Deal verloren',
    message: `"${opportunityName}" wurde als verloren markiert${reason ? `: ${reason}` : ''}.`,
    severity: 'warning' as AlertColor,
    duration: 4000
  }),

  /**
   * System notifications
   */
  autoSaved: () => ({
    message: '√Ñnderungen automatisch gespeichert',
    severity: 'info' as AlertColor,
    duration: 2000
  }),

  syncError: (retryAction: () => void) => ({
    title: 'Synchronisation fehlgeschlagen',
    message: 'Verbindung zum Server unterbrochen. Versuchen Sie es erneut.',
    severity: 'error' as AlertColor,
    persistent: true,
    action: {
      label: 'Wiederholen',
      onClick: retryAction
    }
  }),

  /**
   * Contract monitoring notifications
   */
  contractExpiring: (customerName: string, daysLeft: number) => ({
    title: 'Vertrag l√§uft aus',
    message: `Vertrag mit "${customerName}" l√§uft in ${daysLeft} Tagen aus.`,
    severity: 'warning' as AlertColor,
    duration: 6000,
    action: {
      label: 'Renewal starten',
      onClick: () => {
        // Start renewal process
        console.log('Start renewal for', customerName);
      }
    }
  })
};

export default ToastProvider;

import { ReactNode, useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { useLanguage } from '../../i18n/hooks';
import { formatCurrency } from '../../i18n/formatters';
import '../../styles/legacy/calculator.css';
import '../../styles/legacy/slider.css';
import '../../styles/legacy/calculator-components.css';
import '../../styles/legacy/calculator-layout.css';
import { CustomSlider } from './CustomSlider';
import { useCalculateDiscount } from '../../features/calculator/api/calculatorQueries';

interface CalculatorLayoutProps {
  leftContent?: ReactNode;
  rightContent?: ReactNode;
}

export function CalculatorLayout({ leftContent, rightContent }: CalculatorLayoutProps) {
  const { t } = useTranslation('calculator');
  const { currentLanguage } = useLanguage();
  const [orderValue, setOrderValue] = useState(15000);
  const [leadTime, setLeadTime] = useState(14);
  const [pickup, setPickup] = useState(false);
  const calculateDiscount = useCalculateDiscount();

  // Berechnung ausl√∂sen bei √Ñnderungen
  useEffect(() => {
    calculateDiscount.mutate({
      orderValue,
      leadTime,
      pickup,
      chain: false, // TODO: Kettenkundenrabatt implementieren
    });
  }, [orderValue, leadTime, pickup]); // calculateDiscount.mutate ist stabil

  // Handler f√ºr Beispielszenarien
  const applyScenario = (
    scenarioOrderValue: number,
    scenarioLeadTime: number,
    scenarioPickup: boolean
  ) => {
    setOrderValue(scenarioOrderValue);
    setLeadTime(scenarioLeadTime);
    setPickup(scenarioPickup);
  };

  // Ergebnisse aus der API
  const result = calculateDiscount.data;
  const baseDiscount = result?.baseDiscount || 0;
  const earlyBooking = result?.earlyDiscount || 0;
  const pickupDiscount = result?.pickupDiscount || 0;
  const totalDiscount = result?.totalDiscount || 0;
  const savings = result?.savingsAmount || 0;
  const finalPrice = result?.finalPrice || orderValue;

  return (
    <div className="customer-container">
      <h2 className="section-title">{t('title')}</h2>

      <div className="demonstrator-container">
        {/* Linke Seite - Kalkulator */}
        <div className="calculator-section">
          {leftContent || (
            <div>
              {/* Bestellwert Slider */}
              <div className="form-group">
                <div className="slider-label-container">
                  <span id="orderValue-label">{t('sliders.orderValue')}</span>
                  <span className="slider-value-display">
                    {formatCurrency(orderValue, currentLanguage)}
                  </span>
                </div>
                {/* Barrierefreier Custom Slider mit Radix UI */}
                <CustomSlider
                  value={orderValue}
                  onValueChange={setOrderValue}
                  min={1000}
                  max={100000}
                  step={1000}
                  aria-label={t('sliders.orderValue')}
                  aria-labelledby="orderValue-label"
                />
              </div>

              {/* Vorlaufzeit Slider */}
              <div className="form-group">
                <div className="slider-label-container">
                  <span id="leadTime-label">{t('sliders.leadTime')}</span>
                  <span className="slider-value-display">
                    {t('sliders.leadTimeDays', { count: leadTime })}
                  </span>
                </div>
                {/* Barrierefreier Custom Slider mit Radix UI */}
                <CustomSlider
                  value={leadTime}
                  onValueChange={setLeadTime}
                  min={1}
                  max={50}
                  step={1}
                  aria-label={t('sliders.leadTime')}
                  aria-labelledby="leadTime-label"
                />
              </div>

              {/* Abholung Checkbox */}
              <div className="form-group checkbox-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    className="checkbox"
                    checked={pickup}
                    onChange={e => setPickup(e.target.checked)}
                  />
                  <span>
                    {t('checkboxes.pickup', { minValue: formatCurrency(5000, currentLanguage) })}
                  </span>
                </label>
              </div>

              {/* Results Container - Grauer Bereich */}
              <div className="calculator-results-container">
                {/* Result Grid */}
                <div className="calculator-result-grid">
                  <div className="calculator-result-item">
                    <span className="calculator-result-label">{t('results.baseDiscount')}</span>
                    <span className="calculator-result-value">{baseDiscount}%</span>
                  </div>
                  <div className="calculator-result-item">
                    <span className="calculator-result-label">{t('results.earlyBooking')}</span>
                    <span className="calculator-result-value">{earlyBooking}%</span>
                  </div>
                  <div className="calculator-result-item">
                    <span className="calculator-result-label">{t('results.pickupDiscount')}</span>
                    <span className="calculator-result-value">{pickupDiscount}%</span>
                  </div>
                </div>

                {/* Total Discount */}
                <div className="total-discount">
                  <span>{t('results.totalDiscount')}</span>
                  <span className="total-value">{totalDiscount}%</span>
                </div>

                {/* Savings Display */}
                <div className="savings-display">
                  <div className="savings-item">
                    <span>{t('results.savings')}</span>
                    <span className="savings-value">
                      {formatCurrency(savings, currentLanguage)}
                    </span>
                  </div>
                  <div className="savings-item highlight">
                    <span>{t('results.finalPrice')}</span>
                    <span className="savings-value">
                      {formatCurrency(finalPrice, currentLanguage)}
                    </span>
                  </div>
                </div>
              </div>

              {/* Maximaler Gesamtrabatt */}
              <div className="max-discount-info">{t('info.maxDiscount', { max: 15 })}</div>
            </div>
          )}
        </div>

        {/* Rechte Seite - Info */}
        <div className="info-section">
          {rightContent || (
            <>
              {/* 1. Rabattsystem Details */}
              <div className="info-card">
                <div className="rabattsystem-grid">
                  {/* Basisrabatt - 3 Spalten Tabelle */}
                  <div>
                    <h3>{t('results.baseDiscount')}</h3>
                    <table className="rabatt-table">
                      <tbody>
                        <tr>
                          <td>{currentLanguage === 'de' ? 'ab' : 'from'}</td>
                          <td className="value-column">{formatCurrency(5000, currentLanguage)}</td>
                          <td className="percent-column">3%</td>
                        </tr>
                        <tr>
                          <td>{currentLanguage === 'de' ? 'ab' : 'from'}</td>
                          <td className="value-column">{formatCurrency(15000, currentLanguage)}</td>
                          <td className="percent-column">6%</td>
                        </tr>
                        <tr>
                          <td>{currentLanguage === 'de' ? 'ab' : 'from'}</td>
                          <td className="value-column">{formatCurrency(30000, currentLanguage)}</td>
                          <td className="percent-column">8%</td>
                        </tr>
                        <tr>
                          <td>{currentLanguage === 'de' ? 'ab' : 'from'}</td>
                          <td className="value-column">{formatCurrency(50000, currentLanguage)}</td>
                          <td className="percent-column">9%</td>
                        </tr>
                        <tr>
                          <td>{currentLanguage === 'de' ? 'ab' : 'from'}</td>
                          <td className="value-column">{formatCurrency(75000, currentLanguage)}</td>
                          <td className="percent-column">10%</td>
                        </tr>
                      </tbody>
                    </table>
                  </div>
                  {/* Fr√ºhbucherrabatt - 2 Spalten Tabelle */}
                  <div>
                    <h3>{t('results.earlyBooking')}</h3>
                    <table className="rabatt-table">
                      <tbody>
                        <tr>
                          <td>{currentLanguage === 'de' ? 'ab 10 Tage' : 'from 10 days'}</td>
                          <td className="percent-column">1%</td>
                        </tr>
                        <tr>
                          <td>{currentLanguage === 'de' ? 'ab 15 Tage' : 'from 15 days'}</td>
                          <td className="percent-column">2%</td>
                        </tr>
                        <tr>
                          <td>{currentLanguage === 'de' ? 'ab 30 Tage' : 'from 30 days'}</td>
                          <td className="percent-column">3%</td>
                        </tr>
                        <tr>
                          <td>&nbsp;</td>
                          <td>&nbsp;</td>
                        </tr>
                        <tr>
                          <td>{t('results.pickupDiscount')}</td>
                          <td className="percent-column">2%</td>
                        </tr>
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>

              {/* 2. Kettenkundenregelung */}
              <div className="info-card">
                <h3>
                  {currentLanguage === 'de' ? 'Kettenkundenregelung' : 'Chain Customer Policy'}
                </h3>
                <p>
                  {currentLanguage === 'de'
                    ? 'F√ºr Unternehmen mit mehreren Standorten (z.B. Hotel- oder Klinikgruppen):'
                    : 'For companies with multiple locations (e.g. hotel or clinic groups):'}
                </p>
                <div className="option-container">
                  <div className="option-box">
                    <strong className="option-label">
                      {currentLanguage === 'de' ? 'Option A:' : 'Option A:'}
                    </strong>{' '}
                    <span className="option-text">
                      {currentLanguage === 'de'
                        ? 'Bestellungen verschiedener Standorte innerhalb einer'
                        : 'Orders from different locations within one'}
                    </span>
                    <div className="option-indent">
                      {currentLanguage === 'de'
                        ? 'Woche werden zusammengerechnet'
                        : 'week are combined'}
                    </div>
                  </div>
                  <div className="option-box">
                    <strong className="option-label">
                      {currentLanguage === 'de' ? 'Option B:' : 'Option B:'}
                    </strong>{' '}
                    <span className="option-text">
                      {currentLanguage === 'de'
                        ? 'Zentrale Bestellung mit Mehrfachauslieferung'
                        : 'Central order with multiple deliveries'}
                    </span>
                  </div>
                </div>
              </div>

              {/* 3. Beispielszenarien */}
              <div className="info-card gradient">
                <h3>{t('scenarios.title')}</h3>
                <div className="scenario-grid">
                  {/* Hotelkette */}
                  <div className="scenario-card" onClick={() => applyScenario(35000, 21, true)}>
                    <div className="scenario-header">
                      <span className="scenario-icon">üè®</span>
                      <strong className="scenario-title">{t('scenarios.hotel')}</strong>
                    </div>
                    <div className="scenario-content">
                      <div className="scenario-details">
                        {formatCurrency(35000, currentLanguage)}
                        <span className="separator">‚Ä¢</span>
                        21 {currentLanguage === 'de' ? 'Tage' : 'days'}
                        <span className="separator">‚Ä¢</span>
                        {t('calculator.deliveryOptions.pickup')}
                      </div>
                      <div className="scenario-discount">
                        12% {currentLanguage === 'de' ? 'Rabatt' : 'discount'}
                      </div>
                    </div>
                  </div>

                  {/* Klinikgruppe */}
